app.controller("contactController",["$scope","contactFactory",(e,t)=>{$("#contactForm").form({keyboardShortcuts:!1,on:"blur",inline:!0,fields:{title:{identifier:"name",rules:[{type:"empty",prompt:"Please enter a name."}]},email:{identifier:"email",rules:[{type:"empty",prompt:"Please enter a email."},{type:"email",prompt:"Please enter a valid email."}]},subject:{identifier:"subject",rules:[{type:"empty",prompt:"Please enter a subject."},{type:"maxLength[300]",prompt:"Subject can be up to {ruleValue} characters long."}]},message:{identifier:"message",rules:[{type:"empty",prompt:"Please enter a message."},{type:"maxLength[2000]",prompt:"Your message can be up to {ruleValue} characters long."}]}},onSuccess:()=>{e.onSubmit()}}),e.contactForm={},e.onSubmit=(()=>{e.loadingContact=!0,t.sendMessage(Object.assign(e.contactForm,{recaptcha:e.recaptchaResponse})).then(t=>{console.log(t),e.loadingContact=!1})}),e.activeSubmitBtn=!1,e.successCaptcha=(t=>{e.recaptchaResponse=t,e.activeSubmitBtn=!0})}]);