app.controller("eventDetailController",["$scope","favFactory",(e,t)=>{e.init=((a,s)=>{"null"!==a&&t.isFav(a,s).then(t=>{e.eventIsFav=t.isFav})}),e.addFavourites=((a,s)=>{t.addFavourites(a,s,1).then(t=>{t.status&&(e.eventIsFav=!0)})}),e.delFavourites=((a,s)=>{t.delFavourites(a,s).then(t=>{t.status&&(e.eventIsFav=!1)})})}]);