app.directive("countryAndCategoryDropdowns",()=>({template:'<div class="field">\n  <label>Category</label>\n  <select name="category" ng-model="newAdForm.category" ng-options="index as category.name for (index, category) in categories " ng-change="changeCategory()">  <input type="hidden" ng-model="categories[newAdForm.category].name" name="categoryName" /> <option value="">Please select</option></select>\n</div>\n<div class="field">\n  <label>Child Category</label>\n  <select name="subCategory" ng-disabled="visiblesCategories.subCategory" ng-model="newAdForm.categoryChild" ng-options="index as subCategory.name for (index, subCategory) in categories[newAdForm.category].subCategories">  <input type="hidden" ng-model="categories[newAdForm.category].subCategories[newAdForm.categoryChild].name" name="subCategoryName" /> <option value="">Please select</option></select>\n</div>',link:function(e){e.visiblesCountries={cities:!0,districts:!0},e.changeCountry=(()=>{e.visiblesCountries.cities=!1}),e.changeCity=(()=>{e.visiblesCountries.districts=!1}),e.visiblesCategories={subCategory:!0},e.changeCategory=(()=>{e.visiblesCategories.subCategory=!1})}}));